    def __init__(self, web3, contract):
        self.CONTRACT_ADDRESS = contract
        super().__init__(web3, {})

    async def balance_of(self, address: str, native: bool = True):
        balance = await self.contract.functions.balanceOf(self.web3.to_checksum_address(address)).call()
        if not native:
            balance = balance / 10 ** (await self.decimals())
        return balance

    async def allowance(self, address: str, contract_address: str, native: bool = True):
        allowance = await self.contract.functions.allowance(address, contract_address).call()
        if not native:
            decimals = await self.decimals()
            allowance = allowance / 10 ** decimals
        return allowance

    # def amount_to_native(self, amount):
    #     return int(amount * 10 ** self.decimals)
    #
    # def native_to_amount(self, native_amount):
    #     return native_amount / 10 ** self.decimals

    async def approve(self, account, contract_address: str, amount: int = None):
        if not amount:
            amount = self.web3.to_wei(2 ** 64 - 1, 'ether')
        tx_call = self.contract.functions.approve(contract_address, amount)
        tx = await self.build_transaction(account, tx_call, 0)
        return await self.send_and_wait_for_transaction(account, tx)

    async def transfer(self, account, to_address: str, amount: int):
        tx_call = self.contract.functions.transfer(to_address, amount)
        tx = await self.build_transaction(account, tx_call, 0)
        return await self.send_and_wait_for_transaction(account, tx)

    async def decimals(self):
        return await self.contract.functions.decimals().call()

    async def symbol(self):
        return await self.contract.functions.symbol().call()
